import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [techFilter, setTechFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const res = await axios.get('/api/projects');
      // Filter out projects owned by blocked users
      const filteredProjects = res.data.data.filter(project =>
        project.owner && !project.owner.isBlocked
      );
      setProjects(filteredProjects);
    } catch (error) {
      console.error('Error fetching projects:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredProjects = projects.filter(project => {
    const matchesSearch = project.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         project.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesTech = !techFilter || project.techStack.some(tech =>
      tech.toLowerCase().includes(techFilter.toLowerCase())
    );
    const matchesStatus = !statusFilter || project.status === statusFilter;

    return matchesSearch && matchesTech && matchesStatus;
  });

  const handleJoinRequest = async (projectId) => {
    try {
      await axios.post(`/api/projects/${projectId}/join`, {
        role: 'Developer', // Default role, could be made selectable
        message: 'I would like to join this project.'
      });
      // Refresh projects to show updated join requests
      fetchProjects();
    } catch (error) {
      console.error('Error sending join request:', error);
    }
  };

  const isOwner = (project) => project.owner && project.owner._id === user?.id;
  const hasRequested = (project) => project.joinRequests.some(req => req.user && req.user._id === user?.id);
  const isCollaborator = (project) => project.collaborators.some(collab => collab.user && collab.user._id === user?.id);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 relative">
      {/* Background Image */}
      <div
        className="absolute inset-0 bg-cover bg-center bg-no-repeat opacity-10"
        style={{
          backgroundImage: `url('https://images.unsplash.com/photo-1555949963-aa79dcee981c?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')`
        }}
      ></div>

      <div className="relative max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Projects</h1>
          <Link
            to="/create-project"
            className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700"
          >
            Create New Project
          </Link>
        </div>

        {/* Filters */}
        <div className="bg-white shadow rounded-lg p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                Search
              </label>
              <input
                type="text"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search projects..."
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label htmlFor="tech" className="block text-sm font-medium text-gray-700 mb-1">
                Technology
              </label>
              <input
                type="text"
                id="tech"
                value={techFilter}
                onChange={(e) => setTechFilter(e.target.value)}
                placeholder="Filter by tech..."
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label htmlFor="status" className="block text-sm font-medium text-gray-700 mb-1">
                Status
              </label>
              <select
                id="status"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">All Status</option>
                <option value="Idea">Idea</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>
          </div>
        </div>

        {/* Projects Grid */}
        {filteredProjects.length === 0 ? (
          <div className="bg-white shadow rounded-lg p-6 text-center">
            <p className="text-gray-500">No projects found matching your criteria.</p>
          </div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {filteredProjects.map(project => (
              <div key={project._id} className="bg-white shadow rounded-lg p-6">
                <div className="flex justify-between items-start mb-4">
                  <h3 className="text-lg font-semibold">{project.title}</h3>
                  <span className={`px-2 py-1 text-xs rounded-full ${
                    project.status === 'Idea' ? 'bg-yellow-100 text-yellow-800' :
                    project.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                    'bg-green-100 text-green-800'
                  }`}>
                    {project.status}
                  </span>
                </div>

                <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                  {project.description}
                </p>

                {/* Tech Stack */}
                <div className="mb-4">
                  <div className="flex flex-wrap gap-1">
                    {project.techStack.slice(0, 3).map((tech, index) => (
                      <span
                        key={index}
                        className="px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded"
                      >
                        {tech}
                      </span>
                    ))}
                    {project.techStack.length > 3 && (
                      <span className="px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded">
                        +{project.techStack.length - 3} more
                      </span>
                    )}
                  </div>
                </div>

                {/* Required Roles */}
                <div className="mb-4">
                  <p className="text-sm text-gray-500 mb-1">Looking for:</p>
                  <div className="flex flex-wrap gap-1">
                    {project.requiredRoles.map((role, index) => (
                      <span
                        key={index}
                        className={`px-2 py-1 text-xs rounded ${
                          role.filled ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                        }`}
                      >
                        {role.role}
                      </span>
                    ))}
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <div className="text-sm text-gray-500">
                    By {project.owner ? project.owner.name : 'Unknown Owner'}
                  </div>
                  <div className="flex space-x-2">
                    <Link
                      to={`/projects/${project._id}`}
                      className="text-blue-600 hover:text-blue-500 text-sm"
                    >
                      View Details
                    </Link>
                    {!isOwner(project) && !isCollaborator(project) && !hasRequested(project) && (
                      <button
                        onClick={() => handleJoinRequest(project._id)}
                        className="text-green-600 hover:text-green-500 text-sm"
                      >
                        Join
                      </button>
                    )}
                    {hasRequested(project) && (
                      <span className="text-yellow-600 text-sm">Requested</span>
                    )}
                    {isCollaborator(project) && (
                      <span className="text-green-600 text-sm">Joined</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  </div>
  );
};

export default Projects;
