import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import NotificationPopup from './NotificationPopup';

const Navbar = () => {
  const { user, logout, isAuthenticated, isAdmin, getNotifications } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotificationDropdown, setShowNotificationDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      fetchNotifications();
    }
  }, [isAuthenticated]);

  const fetchNotifications = async () => {
    const result = await getNotifications();
    if (result.success) {
      setNotifications(result.data.data);
      setUnreadCount(result.data.data.filter(n => !n.read).length);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleNotificationDropdown = () => {
    setShowNotificationDropdown(!showNotificationDropdown);
  };

  const markAsRead = async (id) => {
    // Implement mark as read functionality
    await fetchNotifications();
  };

  return (
    <nav className="bg-gradient-to-r from-slate-900 via-blue-900 to-slate-900 shadow-xl sticky top-0 z-[99999] border-b border-slate-700">

      <div className="relative max-w-7xl mx-auto px-4 z-[99999]">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center group">
              <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center mr-3 group-hover:scale-105 transition-transform duration-200">
                <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M12.316 3.051a1 1 0 01.633 1.265l-4 12a1 1 0 11-1.898-.632l4-12a1 1 0 011.265-.633zM5.707 6.293a1 1 0 010 1.414L3.414 10l2.293 2.293a1 1 0 11-1.414 1.414l-3-3a1 1 0 010-1.414l3-3a1 1 0 011.414 0zm8.586 0a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 11-1.414-1.414L16.586 10l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                </svg>
              </div>
              <span className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">DevConnect</span>
            </Link>
          </div>

          <div className="hidden md:flex items-center space-x-6">
            <Link to="/" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
              Home
            </Link>
            <Link to="/developers" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
              Developers
            </Link>

            {isAuthenticated ? (
              <>
                <Link to="/projects" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
                  Projects
                </Link>
                <Link to="/dashboard" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
                  Dashboard
                </Link>
                {isAdmin && (
                  <Link to="/admin" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
                    Admin
                  </Link>
                )}
                {/* Notifications */}
                <div className="relative">
                  <button
                    onClick={toggleNotificationDropdown}
                    className="relative p-2 text-slate-300 hover:text-white transition-colors duration-200"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5-5V7a3 3 0 00-6 0v5l-5 5h5m0 0v1a3 3 0 006 0v-1m-6 0h6" />
                    </svg>
                    {unreadCount > 0 && (
                      <span className="absolute -top-1 -right-1 bg-gradient-to-r from-red-500 to-pink-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-semibold shadow-lg">
                        {unreadCount > 9 ? '9+' : unreadCount}
                      </span>
                    )}
                  </button>

                  {showNotificationDropdown && (
                    <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg z-[999999] border border-gray-200">
                      <div className="py-1">
                        <div className="px-4 py-2 border-b">
                          <h3 className="text-sm font-medium text-gray-900">Notifications</h3>
                        </div>
                        <div className="max-h-64 overflow-y-auto">
                          {notifications.length > 0 ? (
                            notifications.slice(0, 5).map((notification) => (
                              <div key={notification._id} className={`px-4 py-3 border-b hover:bg-gray-50 ${!notification.read ? 'bg-blue-50' : ''}`}>
                                <div className="flex justify-between items-start">
                                  <div className="flex-1">
                                    <p className="text-sm font-medium text-gray-900">{notification.title}</p>
                                    <p className="text-sm text-gray-600">{notification.message}</p>
                                    <p className="text-xs text-gray-400 mt-1">
                                      {new Date(notification.createdAt).toLocaleDateString()}
                                    </p>
                                  </div>
                                  {!notification.read && (
                                    <button
                                      onClick={() => markAsRead(notification._id)}
                                      className="ml-2 text-blue-600 hover:text-blue-800 text-xs"
                                    >
                                      Mark read
                                    </button>
                                  )}
                                </div>
                              </div>
                            ))
                          ) : (
                            <div className="px-4 py-3 text-sm text-gray-500">
                              No notifications
                            </div>
                          )}
                        </div>
                        <div className="px-4 py-2 border-t">
                          <Link
                            to="/dashboard"
                            onClick={() => setShowNotificationDropdown(false)}
                            className="text-sm text-blue-600 hover:text-blue-800"
                          >
                            View all notifications
                          </Link>
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                {/* User Menu */}
                <div className="relative">
                  <button
                    onClick={() => setShowUserMenu(!showUserMenu)}
                    className="flex items-center text-slate-300 hover:text-white transition-colors duration-200 bg-slate-800/50 hover:bg-slate-700/50 px-3 py-2 rounded-lg"
                  >
                    <div className="w-6 h-6 bg-gradient-to-br from-blue-400 to-purple-500 rounded-full flex items-center justify-center mr-2">
                      <span className="text-xs font-semibold text-white">{user?.name?.charAt(0).toUpperCase()}</span>
                    </div>
                    <span className="text-sm font-medium">{user?.name}</span>
                    <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>

                  {showUserMenu && (
                    <div className="absolute right-0 mt-2 w-48 bg-slate-800 rounded-lg shadow-xl z-[999999] border border-slate-600">
                      <div className="py-2">
                        <Link to="/profile" className="block px-4 py-2 text-sm text-slate-300 hover:text-white hover:bg-slate-700/50 transition-colors duration-200" onClick={() => setShowUserMenu(false)}>
                          Profile
                        </Link>
                        <Link to="/chat" className="block px-4 py-2 text-sm text-slate-300 hover:text-white hover:bg-slate-700/50 transition-colors duration-200" onClick={() => setShowUserMenu(false)}>
                          Chat
                        </Link>
                        <button
                          onClick={() => { handleLogout(); setShowUserMenu(false); }}
                          className="block w-full text-left px-4 py-2 text-sm text-slate-300 hover:text-white hover:bg-slate-700/50 transition-colors duration-200"
                        >
                          Logout
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <>
                <Link to="/login" className="text-slate-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 hover:bg-slate-800/50">
                  Login
                </Link>
                <Link to="/register" className="bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200 transform hover:scale-105 shadow-lg">
                  Sign Up
                </Link>
              </>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
            >
              <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile menu */}
        {isOpen && (
          <div className="md:hidden bg-slate-800/95 backdrop-blur-sm border-t border-slate-700">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              <Link to="/" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                Home
              </Link>
              <Link to="/developers" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                Developers
              </Link>

              {isAuthenticated ? (
                <>
                  <Link to="/projects" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                    Projects
                  </Link>
                  <Link to="/dashboard" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                    Dashboard
                  </Link>
                  {isAdmin && (
                    <Link to="/admin" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                      Admin
                    </Link>
                  )}
                  <Link to="/profile" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                    Profile
                  </Link>
                  <Link to="/chat" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                    Chat
                  </Link>
                  <button
                    onClick={() => { handleLogout(); setIsOpen(false); }}
                    className="block w-full text-left text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200"
                  >
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link to="/login" className="block text-slate-300 hover:text-white hover:bg-slate-700/50 px-3 py-2 rounded-md transition-colors duration-200" onClick={() => setIsOpen(false)}>
                    Login
                  </Link>
                  <Link to="/register" className="block bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white px-3 py-2 rounded-md transition-all duration-200 transform hover:scale-105 shadow-lg" onClick={() => setIsOpen(false)}>
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
