import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Developers = () => {
  const [developers, setDevelopers] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [skillFilter, setSkillFilter] = useState('');
  const [experienceFilter, setExperienceFilter] = useState('');
  const [showAdmins, setShowAdmins] = useState(false);

  useEffect(() => {
    fetchDevelopers();
  }, []);

  const fetchDevelopers = async () => {
    try {
      const res = await axios.get('/api/users');
      // Separate admin and non-admin users
      const adminUsers = res.data.data.filter(user => user.role === 'admin');
      const nonAdminUsers = res.data.data.filter(user => user.role !== 'admin');
      setAdmins(adminUsers);
      setDevelopers(nonAdminUsers);
    } catch (error) {
      console.error('Error fetching developers:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredDevelopers = developers.filter(developer => {
    const matchesSearch = developer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         developer.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesSkill = !skillFilter || developer.profile?.skills?.some(skill =>
      skill.toLowerCase().includes(skillFilter.toLowerCase())
    );
    const matchesExperience = !experienceFilter || developer.profile?.experience === experienceFilter;

    return matchesSearch && matchesSkill && matchesExperience;
  });

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Find Developers</h1>

        {/* Filters */}
        <div className="bg-white shadow rounded-lg p-6 mb-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
                Search
              </label>
              <input
                type="text"
                id="search"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search by name or email..."
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label htmlFor="skill" className="block text-sm font-medium text-gray-700 mb-1">
                Skill
              </label>
              <input
                type="text"
                id="skill"
                value={skillFilter}
                onChange={(e) => setSkillFilter(e.target.value)}
                placeholder="Filter by skill..."
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
            <div>
              <label htmlFor="experience" className="block text-sm font-medium text-gray-700 mb-1">
                Experience Level
              </label>
              <select
                id="experience"
                value={experienceFilter}
                onChange={(e) => setExperienceFilter(e.target.value)}
                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">All Levels</option>
                <option value="Beginner">Beginner</option>
                <option value="Intermediate">Intermediate</option>
                <option value="Advanced">Advanced</option>
                <option value="Expert">Expert</option>
              </select>
            </div>
          </div>
        </div>

        {/* Toggle Button */}
        <div className="mb-8">
          <button
            onClick={() => setShowAdmins(!showAdmins)}
            className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
          >
            {showAdmins ? 'Hide Admins' : 'Show Admins'}
          </button>
        </div>

        {/* Admins Section */}
        {showAdmins && admins.length > 0 && (
          <div className="mb-12">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Administrators</h2>
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {admins.map(admin => (
                <div key={admin._id} className="bg-white shadow rounded-lg p-6 border-2 border-red-200">
                  <div className="flex items-center mb-4">
                    {admin.profile?.profilePicture ? (
                      <img
                        src={admin.profile.profilePicture}
                        alt={admin.name}
                        className="w-12 h-12 rounded-full object-cover"
                      />
                    ) : (
                      <div className="w-12 h-12 bg-red-500 rounded-full flex items-center justify-center text-white font-semibold text-lg">
                        {admin.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                    <div className="ml-4">
                      <h3 className="text-lg font-semibold">{admin.name}</h3>
                      <p className="text-gray-600 text-sm">{admin.email}</p>
                      <span className="inline-block px-2 py-1 text-xs bg-red-100 text-red-800 rounded-full mt-1">
                        Administrator
                      </span>
                    </div>
                  </div>

                  {/* Bio */}
                  {admin.profile?.bio && (
                    <p className="text-gray-700 text-sm mb-4 line-clamp-3">
                      {admin.profile.bio}
                    </p>
                  )}

                  {/* Skills */}
                  {admin.profile?.skills && admin.profile.skills.length > 0 && (
                    <div className="mb-4">
                      <p className="text-sm font-medium text-gray-700 mb-2">Skills:</p>
                      <div className="flex flex-wrap gap-1">
                        {admin.profile.skills.slice(0, 4).map((skill, index) => (
                          <span
                            key={index}
                            className="px-2 py-1 text-xs bg-red-100 text-red-800 rounded"
                          >
                            {skill}
                          </span>
                        ))}
                        {admin.profile.skills.length > 4 && (
                          <span className="px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded">
                            +{admin.profile.skills.length - 4} more
                          </span>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Links */}
                  <div className="flex space-x-4 text-sm">
                    {admin.profile?.github && (
                      <a
                        href={admin.profile.github}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-500"
                      >
                        GitHub
                      </a>
                    )}
                    {admin.profile?.portfolio && (
                      <a
                        href={admin.profile.portfolio}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-500"
                      >
                        Portfolio
                      </a>
                    )}
                  </div>

                  {/* Action */}
                  <div className="mt-4 pt-4 border-t">
                    <Link
                      to={`/profile/${admin._id}`}
                      className="text-blue-600 hover:text-blue-500 text-sm"
                    >
                      View Full Profile
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Developers Section */}
        <div>
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Developers</h2>
          {filteredDevelopers.length === 0 ? (
            <div className="bg-white shadow rounded-lg p-6 text-center">
              <p className="text-gray-500">No developers found matching your criteria.</p>
            </div>
          ) : (
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {filteredDevelopers.map(developer => (
                <div key={developer._id} className="bg-white shadow rounded-lg p-6">
                  <div className="flex items-center mb-4">
                    {developer.profile?.profilePicture ? (
                      <img
                        src={developer.profile.profilePicture}
                        alt={developer.name}
                        className="w-12 h-12 rounded-full object-cover"
                      />
                    ) : (
                      <div className="w-12 h-12 bg-blue-500 rounded-full flex items-center justify-center text-white font-semibold text-lg">
                        {developer.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                    <div className="ml-4">
                      <h3 className="text-lg font-semibold">{developer.name}</h3>
                      <p className="text-gray-600 text-sm">{developer.email}</p>
                    </div>
                  </div>

                  {/* Bio */}
                  {developer.profile?.bio && (
                    <p className="text-gray-700 text-sm mb-4 line-clamp-3">
                      {developer.profile.bio}
                    </p>
                  )}

                  {/* Skills */}
                  {developer.profile?.skills && developer.profile.skills.length > 0 && (
                    <div className="mb-4">
                      <p className="text-sm font-medium text-gray-700 mb-2">Skills:</p>
                      <div className="flex flex-wrap gap-1">
                        {developer.profile.skills.slice(0, 4).map((skill, index) => (
                          <span
                            key={index}
                            className="px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded"
                          >
                            {skill}
                          </span>
                        ))}
                        {developer.profile.skills.length > 4 && (
                          <span className="px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded">
                            +{developer.profile.skills.length - 4} more
                          </span>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Experience Level */}
                  {developer.profile?.experience && (
                    <div className="mb-4">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        developer.profile.experience === 'Beginner' ? 'bg-green-100 text-green-800' :
                        developer.profile.experience === 'Intermediate' ? 'bg-yellow-100 text-yellow-800' :
                        developer.profile.experience === 'Advanced' ? 'bg-orange-100 text-orange-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {developer.profile.experience}
                      </span>
                    </div>
                  )}

                  {/* Links */}
                  <div className="flex space-x-4 text-sm">
                    {developer.profile?.github && (
                      <a
                        href={developer.profile.github}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-500"
                      >
                        GitHub
                      </a>
                    )}
                    {developer.profile?.portfolio && (
                      <a
                        href={developer.profile.portfolio}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 hover:text-blue-500"
                      >
                        Portfolio
                      </a>
                    )}
                  </div>

                  {/* Action */}
                  <div className="mt-4 pt-4 border-t">
                    <Link
                      to={`/profile/${developer._id}`}
                      className="text-blue-600 hover:text-blue-500 text-sm"
                    >
                      View Full Profile
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Developers;
