import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const UserProfile = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserProfile();
  }, [id]);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(`/api/users/${id}`);
      setUser(response.data.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="text-center py-12">
        <h3 className="text-xl font-medium text-gray-900 mb-2">User not found</h3>
        <p className="text-gray-600">The user profile you're looking for doesn't exist.</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="bg-white rounded-lg shadow-md p-8">
        {/* Profile Header */}
        <div className="flex items-center mb-8">
          <div className="w-24 h-24 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center mr-6 overflow-hidden">
            {user.profile?.avatar ? (
              <img
                src={user.profile.avatar}
                alt="Profile"
                className="w-full h-full object-cover"
              />
            ) : (
              <span className="text-white text-3xl font-semibold">
                {user.name.charAt(0).toUpperCase()}
              </span>
            )}
          </div>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">{user.name}</h1>
            <p className="text-gray-600 text-lg">{user.email}</p>
            <p className="text-gray-500 text-sm capitalize">{user.role}</p>
          </div>
        </div>

        {/* Profile Details */}
        {user.profile && (
          <div className="space-y-6">
            {/* Bio */}
            {user.profile.bio && (
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">About</h3>
                <p className="text-gray-700">{user.profile.bio}</p>
              </div>
            )}

            {/* Experience */}
            {user.profile.experience && (
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Experience Level</h3>
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                  user.profile.experience === 'Expert' ? 'bg-green-100 text-green-800' :
                  user.profile.experience === 'Advanced' ? 'bg-blue-100 text-blue-800' :
                  user.profile.experience === 'Intermediate' ? 'bg-yellow-100 text-yellow-800' :
                  'bg-gray-100 text-gray-800'
                }`}>
                  {user.profile.experience}
                </span>
              </div>
            )}

            {/* Skills */}
            {user.profile.skills && user.profile.skills.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Skills</h3>
                <div className="flex flex-wrap gap-2">
                  {user.profile.skills.map((skill, index) => (
                    <span key={index} className="px-3 py-1 bg-gray-100 text-gray-600 text-sm rounded">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Links */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">Links</h3>
              <div className="flex space-x-4">
                {user.profile.github && (
                  <a
                    href={user.profile.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-gray-900 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-gray-800 transition-colors"
                  >
                    GitHub
                  </a>
                )}
                {user.profile.portfolio && (
                  <a
                    href={user.profile.portfolio}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-blue-700 transition-colors"
                  >
                    Portfolio
                  </a>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="mt-8 flex space-x-4">
          <button
            onClick={() => window.location.href = `/chat/${user._id}`}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg text-sm font-medium hover:bg-blue-700 transition-colors"
            aria-label={`Chat with ${user.name}`}
            title={`Chat with ${user.name}`}
          >
            Chat
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
