import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const NotificationPopup = () => {
  const { user, getNotifications, markNotificationAsRead, deleteNotification } = useAuth();
  const [notifications, setNotifications] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    if (user) {
      fetchNotifications();
    }
  }, [user]);

  const fetchNotifications = async () => {
    const result = await getNotifications();
    if (result.success) {
      const unreadNotifications = result.data.data.filter(n => !n.read);
      setNotifications(unreadNotifications);
      setShowPopup(unreadNotifications.length > 0);
    }
  };

  const handleMarkAsRead = async (id) => {
    const result = await markNotificationAsRead(id);
    if (result.success) {
      setNotifications(notifications.filter(n => n._id !== id));
      if (notifications.length <= 1) {
        setShowPopup(false);
      }
    }
  };

  const handleDelete = async (id) => {
    const result = await deleteNotification(id);
    if (result.success) {
      setNotifications(notifications.filter(n => n._id !== id));
      if (notifications.length <= 1) {
        setShowPopup(false);
      }
    }
  };

  const handleClose = () => {
    setShowPopup(false);
  };

  if (!showPopup || notifications.length === 0) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4 max-h-96 overflow-y-auto">
        <div className="p-4 border-b">
          <div className="flex justify-between items-center">
            <h3 className="text-lg font-semibold text-gray-900">Notifications</h3>
            <button
              onClick={handleClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        <div className="p-4 space-y-3">
          {notifications.map((notification) => (
            <div key={notification._id} className="border rounded-lg p-3 bg-gray-50">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <h4 className="font-medium text-gray-900">{notification.title}</h4>
                  <p className="text-sm text-gray-600 mt-1">{notification.message}</p>
                  <p className="text-xs text-gray-400 mt-2">
                    {new Date(notification.createdAt).toLocaleDateString()}
                  </p>
                </div>
                <div className="flex space-x-2 ml-2">
                  <button
                    onClick={() => handleMarkAsRead(notification._id)}
                    className="text-blue-600 hover:text-blue-800 text-sm"
                  >
                    Mark Read
                  </button>
                  <button
                    onClick={() => handleDelete(notification._id)}
                    className="text-red-600 hover:text-red-800 text-sm"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="p-4 border-t bg-gray-50">
          <Link
            to="/dashboard"
            onClick={handleClose}
            className="block w-full text-center text-blue-600 hover:text-blue-800"
          >
            View All Notifications
          </Link>
        </div>
      </div>
    </div>
  );
};

export default NotificationPopup;
