import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import NotificationPopup from './NotificationPopup';

const Navbar = () => {
  const { user, logout, isAuthenticated, isAdmin, getNotifications } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotificationDropdown, setShowNotificationDropdown] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      fetchNotifications();
    }
  }, [isAuthenticated]);

  const fetchNotifications = async () => {
    const result = await getNotifications();
    if (result.success) {
      setNotifications(result.data.data);
      setUnreadCount(result.data.data.filter(n => !n.read).length);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleNotificationDropdown = () => {
    setShowNotificationDropdown(!showNotificationDropdown);
  };

  const markAsRead = async (id) => {
    // Implement mark as read functionality
    await fetchNotifications();
  };

  return (
    <nav className="bg-white shadow-lg relative overflow-hidden z-[999]">
      {/* Background Image */}
      <div
        className="absolute inset-0 bg-cover bg-center opacity-10"
        style={{
          backgroundImage: `url('https://images.unsplash.com/photo-1551650975-87deedd944c3?ixlib=rb-4.0.3&auto=format&fit=crop&w=1974&q=80')`,
        }}
      ></div>
      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-r from-blue-50 to-purple-50 opacity-20"></div>

      <div className="relative max-w-7xl mx-auto px-4 z-[999]">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center">
              <span className="text-xl font-bold text-blue-600">DevConnect</span>
            </Link>
          </div>

          <div className="hidden md:flex items-center space-x-4">
            <Link to="/" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
              Home
            </Link>
            <Link to="/developers" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
              Developers
            </Link>

            {isAuthenticated ? (
              <>
                <Link to="/projects" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Projects
                </Link>
                <Link to="/dashboard" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Dashboard
                </Link>
                {isAdmin && (
                  <Link to="/admin" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                    Admin
                  </Link>
                )}
                {/* Notifications */}
                <div className="relative">
                  <button
                    onClick={toggleNotificationDropdown}
                    className="relative p-2 text-gray-700 hover:text-blue-600"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5-5V7a3 3 0 00-6 0v5l-5 5h5m0 0v1a3 3 0 006 0v-1m-6 0h6" />
                    </svg>
                    {unreadCount > 0 && (
                      <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                        {unreadCount > 9 ? '9+' : unreadCount}
                      </span>
                    )}
                  </button>

                  {showNotificationDropdown && (
                    <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg z-50">
                      <div className="py-1">
                        <div className="px-4 py-2 border-b">
                          <h3 className="text-sm font-medium text-gray-900">Notifications</h3>
                        </div>
                        <div className="max-h-64 overflow-y-auto">
                          {notifications.length > 0 ? (
                            notifications.slice(0, 5).map((notification) => (
                              <div key={notification._id} className={`px-4 py-3 border-b hover:bg-gray-50 ${!notification.read ? 'bg-blue-50' : ''}`}>
                                <div className="flex justify-between items-start">
                                  <div className="flex-1">
                                    <p className="text-sm font-medium text-gray-900">{notification.title}</p>
                                    <p className="text-sm text-gray-600">{notification.message}</p>
                                    <p className="text-xs text-gray-400 mt-1">
                                      {new Date(notification.createdAt).toLocaleDateString()}
                                    </p>
                                  </div>
                                  {!notification.read && (
                                    <button
                                      onClick={() => markAsRead(notification._id)}
                                      className="ml-2 text-blue-600 hover:text-blue-800 text-xs"
                                    >
                                      Mark read
                                    </button>
                                  )}
                                </div>
                              </div>
                            ))
                          ) : (
                            <div className="px-4 py-3 text-sm text-gray-500">
                              No notifications
                            </div>
                          )}
                        </div>
                        <div className="px-4 py-2 border-t">
                          <Link
                            to="/dashboard"
                            onClick={() => setShowNotificationDropdown(false)}
                            className="text-sm text-blue-600 hover:text-blue-800"
                          >
                            View all notifications
                          </Link>
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                {/* User Menu */}
                <div className="relative">
                  <button
                    onClick={() => setIsOpen(!isOpen)}
                    className="flex items-center text-gray-700 hover:text-blue-600"
                  >
                    <span className="mr-2">{user?.name}</span>
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>

                  {isOpen && (
                    <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-50">
                      <div className="py-1">
                        <Link to="/profile" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                          Profile
                        </Link>
                        <Link to="/chat" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                          Chat
                        </Link>
                        <button
                          onClick={handleLogout}
                          className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                        >
                          Logout
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <>
                <Link to="/login" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Login
                </Link>
                <Link to="/register" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Sign Up
                </Link>
              </>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
            >
              <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile menu */}
        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              <Link to="/" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                Home
              </Link>
              <Link to="/developers" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                Developers
              </Link>

              {isAuthenticated ? (
                <>
                  <Link to="/projects" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Projects
                  </Link>
                  <Link to="/dashboard" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Dashboard
                  </Link>
                  {isAdmin && (
                    <Link to="/admin" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                      Admin
                    </Link>
                  )}
                  <Link to="/profile" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Profile
                  </Link>
                  <Link to="/chat" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Chat
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left text-gray-700 hover:text-blue-600 px-3 py-2"
                  >
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link to="/login" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Login
                  </Link>
                  <Link to="/register" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
