import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import NotificationPopup from './NotificationPopup';

const Navbar = () => {
  const { user, logout, isAuthenticated, isAdmin, getNotifications } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      fetchNotifications();
    }
  }, [isAuthenticated]);

  const fetchNotifications = async () => {
    const result = await getNotifications();
    if (result.success) {
      setNotifications(result.data.data);
      setUnreadCount(result.data.data.filter(n => !n.read).length);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center">
              <span className="text-xl font-bold text-blue-600">DevConnect</span>
            </Link>
          </div>

          <div className="hidden md:flex items-center space-x-4">
            <Link to="/" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
              Home
            </Link>
            <Link to="/developers" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
              Developers
            </Link>
            
            {isAuthenticated ? (
              <>
                <Link to="/projects" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Projects
                </Link>
                <Link to="/dashboard" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Dashboard
                </Link>
                {isAdmin && (
                  <Link to="/admin" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                    Admin
                  </Link>
                )}
                {/* Notifications */}
                <div className="relative">
                  <button
                    onClick={() => {
                      setNotificationsOpen(!notificationsOpen);
                      setIsOpen(false); // Close user menu if open
                    }}
                    className="relative p-2 text-gray-700 hover:text-blue-600"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5v-5zM21 21V11a8 8 0 00-16 0v10H5a2 2 0 01-2-2V9a7 7 0 0114 0v10a2 2 0 01-2 2z" />
                    </svg>
                    {unreadCount > 0 && (
                      <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                        {unreadCount}
                      </span>
                    )}
                  </button>
                  {notificationsOpen && (
                    <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg py-1 z-50 max-h-96 overflow-y-auto">
                      <div className="px-4 py-2 border-b">
                        <h3 className="font-semibold text-gray-900">Notifications</h3>
                      </div>
                      {notifications.length === 0 ? (
                        <div className="px-4 py-2 text-gray-500 text-sm">
                          No notifications yet
                        </div>
                      ) : (
                        notifications.slice(0, 5).map((notification) => (
                          <div
                            key={notification._id}
                            className={`px-4 py-3 border-b hover:bg-gray-50 ${!notification.read ? 'bg-blue-50' : ''}`}
                          >
                            <h4 className="font-medium text-gray-900 text-sm">{notification.title}</h4>
                            <p className="text-xs text-gray-600 mt-1">{notification.message}</p>
                            <p className="text-xs text-gray-400 mt-1">
                              {new Date(notification.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                        ))
                      )}
                      <div className="px-4 py-2 border-t">
                        <Link
                          to="/dashboard"
                          className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                          onClick={() => setNotificationsOpen(false)}
                        >
                          View all notifications â†’
                        </Link>
                      </div>
                    </div>
                  )}
                </div>

                <div className="relative">
                  <button
                    onClick={() => setIsOpen(!isOpen)}
                    className="flex items-center text-gray-700 hover:text-blue-600"
                  >
                    <span className="mr-2">{user?.name}</span>
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </button>
                  {isOpen && (
                    <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1">
                      <Link to="/profile" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                        Profile
                      </Link>
                      <Link to="/chat" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                        Chat
                      </Link>
                      <button
                        onClick={handleLogout}
                        className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                      >
                        Logout
                      </button>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="flex items-center space-x-2">
                <Link to="/login" className="text-gray-700 hover:text-blue-600 px-3 py-2 rounded-md">
                  Login
                </Link>
                <Link to="/register" className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">
                  Sign Up
                </Link>
              </div>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-gray-700 hover:text-blue-600"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Mobile menu */}
        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              <Link to="/" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                Home
              </Link>
              <Link to="/developers" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                Developers
              </Link>
              
              {isAuthenticated ? (
                <>
                  <Link to="/projects" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Projects
                  </Link>
                  <Link to="/dashboard" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Dashboard
                  </Link>
                  {isAdmin && (
                    <Link to="/admin" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                      Admin
                    </Link>
                  )}
                  <Link to="/profile" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Profile
                  </Link>
                  <Link to="/chat" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Chat
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left text-gray-700 hover:text-blue-600 px-3 py-2"
                  >
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link to="/login" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Login
                  </Link>
                  <Link to="/register" className="block text-gray-700 hover:text-blue-600 px-3 py-2">
                    Sign Up
                  </Link>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
